#include <stdint.h>
#include <system_stm32f4xx.h>
#include "timer.h"
#include "Ucglib.h"



static ucg_t ucg;

int main(void)
{
    SystemCoreClockUpdate();           // cập clock
    TimerInit();                       // để khởi tạo chức năng timer.

    // Khởi tạo LCD
    Ucglib4WireSWSPI_begin(&ucg, UCG_FONT_MODE_SOLID);
    ucg_ClearScreen(&ucg);

    ucg_SetFont(&ucg, ucg_font_helvR08_tf);    // lựa chọn font chữ và cỡ chữ là 14
    ucg_SetColor(&ucg, 0, 255, 0, 0);      // chọn màu chữ là màu xanh
    ucg_SetColor(&ucg, 1, 0, 0, 0);            // màu background là màu đen
    ucg_SetRotate180(&ucg);                   // quay màn hình lại 180 độ
    ucg_DrawString(&ucg, 0, 128, 0, "Nhom5 - THCS");      // gần đáy

    /* Loop forever */
    while(1)
    {
        processTimerScheduler();
    }
}
